-- สร้าง temp table เก็บลิสต์ที่ต้องการตรวจสอบ
DECLARE @List TABLE (ClaimHeaderGroupCode VARCHAR(50));


-- หาว่ารายการไหน “ไม่มีใน DB”
SELECT L.ClaimHeaderGroupCode
FROM @List L
WHERE NOT EXISTS (
    SELECT 1
    FROM ClaimPayBack.dbo.ClaimHeaderGroupImport H
    WHERE H.ClaimHeaderGroupCode = L.ClaimHeaderGroupCode
      AND H.IsActive = 1
      AND H.ClaimHeaderGroupImportStatusId <> 2
);
